ontent").css('display','none');			$("#m_content"+$(this).attr('param')).css('display','inline');		});		setInterval('pagetab()',8000);				});	function pagetab()	{		var num=$("#now").val();		$(".m_title").removeClass('select');		$("#m_title"+num).addClass('select');		$(".m_content").css('display','none');		$("#m_content"+num).css('display','inline');		if(parseInt(num)!=4)		{			num=parseInt(num)+1;		}		else		{			num=1;			}		$("#now").attr('value',num);	}</script>